
module.exports = require("proteus").Class.derive({
    
    self: {
        /**
         * We short-circuit here if the Behavior is not intended to be
         * initialized with any arguments.  We just return the prototype of
         * the Behavior.
         */
        initialize: function (instance, args) {
            var proto = this.prototype,
                init  = proto.init
            ;
            
            if (typeof init !== "function") {
                return proto;
            }
            
            return instance;
        }
    }
    
});

