
var Behavior = require("dice-js/behavior"),
    Factory  = require("dice-js/factory"),
    dutil    = require("dice-js/util")
;

new Behavior("HeroNormal", {
    
    results: function (dice, value) {
        return value.map(function (result) {
            var val = { stun: result, body: 0 };
            switch (result) {
                case 1:
                    break;
                case 6:
                    val.body = 2;
                    break;
                default:
                    val.body = 1;
            }
            return val;
        });
    },
    
    result: function (dice, value) {
        return dutil.sumPropertyValues(dice.results);
    }
    
});

new Factory("HeroNormal", 6, "HeroNormal");