
var Dice = require("dice-js"),
    PercentileBehavior = new Dice.Behavior("Percentile", {
        
        abbreviation: "%",
        
        result: function (dice, value) {
            var results = dice.results,
                tens = results[0],
                ones = results[1]
            ;
            
            if (tens === 0 && ones === 0) {
                return 100;
            }
            
            return tens * 10 + ones;
        },
        
        reset: function (dice, value) {
            dice.count = 2;
            dice.faces = 10;
        }
        
    })
;

new Dice.Factory("Percentile", 10, "%");
