
function randomNumber (min, max) {
    return Math.floor(Math.random() * max) + min;
}

function ascendingNumeric (a, b) {
    return a - b;
}

function descendingNumeric (a, b) {
    return b - a;
}

function reduceSum (acc, cur) { return (acc + cur); }

function reduceSumPropertyValues (acc, result) {
    Object.keys(result).forEach(function (key) {
        if (acc.hasOwnProperty(key)) {
            acc[key] += result[key];
        }
        else {
            acc[key] = result[key];
        }
    });
    return acc;
}

function sumPropertyValues (list) {
    return list.reduce(reduceSumPropertyValues, {});
}

module.exports = {
    format:                 require("dice-js/format"),
    randomNumber:           randomNumber,
    ascendingNumeric:       ascendingNumeric,
    descendingNumeric:      descendingNumeric,
    reduceSum:              reduceSum,
    reduceSumPropertyValues:    reduceSumPropertyValues,
    sumPropertyValues:          sumPropertyValues
};
