module.exports = {
    format: require("dice-js/format"),
    
    randomNumber: function (min, max) {
        return Math.floor(Math.random() * max) + min;
    },
    
    ascendingNumeric: function ascendingNumeric (a, b) {
        return a - b;
    },
    
    descendingNumeric: function descendingNumeric (a, b) {
        return b - a;
    },
    
    reduceSum: function reduceSum (t, d) { return (t + d); }
};
